import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';

function App() {
  const [iaData, setIaData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const svgRef = useRef();

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.name.endsWith('.json')) {
      setError('Please select a JSON file.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const text = await file.text();
      const jsonData = JSON.parse(text);

      const response = await fetch('http://localhost:3001/upload', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(jsonData),
      });

      if (response.ok) {
        setIaData(jsonData);
        fetchData();
      } else {
        setError('Failed to upload JSON.');
      }
    } catch (err) {
      setError('Invalid JSON file.');
    } finally {
      setLoading(false);
    }
  };

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:3001/ia');
      if (response.ok) {
        const data = await response.json();
        setIaData(data);
        setError('');
      } else {
        setIaData(null);
        setError('No IA data uploaded yet.');
      }
    } catch (err) {
      setError('Failed to fetch data.');
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (!iaData || error) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const width = 800;
    const height = 600;

    // Add zoom and pan
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      });

    svg.call(zoom);

    const g = svg.append('g');

    // Add defs for arrow markers
    const defs = svg.append('defs');
    defs.append('marker')
      .attr('id', 'arrowhead')
      .attr('viewBox', '-0 -5 10 10')
      .attr('refX', 13)
      .attr('refY', 0)
      .attr('orient', 'auto')
      .attr('markerWidth', 6)
      .attr('markerHeight', 6)
      .attr('xoverflow', 'visible')
      .append('svg:path')
      .attr('d', 'M 0,-5 L 10 ,0 L 0,5')
      .attr('fill', '#999');

    // Add shadow filter
    const filter = defs.append('filter')
      .attr('id', 'shadow')
      .attr('x', '-50%')
      .attr('y', '-50%')
      .attr('width', '200%')
      .attr('height', '200%');

    filter.append('feDropShadow')
      .attr('dx', 2)
      .attr('dy', 2)
      .attr('stdDeviation', 2)
      .attr('flood-color', 'rgba(0,0,0,0.3)');

    const tree = d3.tree().size([height - 100, width - 200]);
    const root = d3.hierarchy(iaData);
    const treeData = tree(root);

    // Links
    const link = g.selectAll('.link')
      .data(treeData.links())
      .enter().append('g')
      .attr('class', 'link');

    link.append('path')
      .attr('d', d3.linkHorizontal()
        .x(d => d.y)
        .y(d => d.x))
      .attr('stroke', '#999')
      .attr('fill', 'none')
      .attr('marker-end', 'url(#arrowhead)')
      .attr('stroke-width', 2);

    // Link labels
    link.append('text')
      .attr('x', d => (d.source.y + d.target.y) / 2)
      .attr('y', d => (d.source.x + d.target.x) / 2 - 5)
      .attr('text-anchor', 'middle')
      .attr('font-size', '12px')
      .attr('fill', '#555')
      .text(d => d.target.data.label || '');

    // Nodes
    const node = g.selectAll('.node')
      .data(treeData.descendants())
      .enter().append('g')
      .classed('node', true)
      .attr('transform', d => `translate(${d.y},${d.x})`)
      .on('mouseover', function(event, d) {
        // Highlight node
        d3.select(this).select('rect').attr('stroke', '#000').attr('stroke-width', 3);
        // Highlight outgoing links
        g.selectAll('.link path').filter(l => l.source === d).attr('stroke', '#000').attr('stroke-width', 4);
      })
      .on('mouseout', function(event, d) {
        d3.select(this).select('rect').attr('stroke', d.data.type === 'decision' ? '#000' : '#fff').attr('stroke-width', 2);
        g.selectAll('.link path').attr('stroke', '#999').attr('stroke-width', 2);
      });

    node.append('rect')
      .attr('width', 120)
      .attr('height', 40)
      .attr('x', -60)
      .attr('y', -20)
      .attr('rx', 5)
      .attr('fill', d => d.data.type === 'decision' ? '#fff' : '#3b82f6')
      .attr('stroke', d => d.data.type === 'decision' ? '#000' : '#fff')
      .attr('stroke-width', 2)
      .attr('filter', 'url(#shadow)');

    node.append('text')
      .attr('text-anchor', 'middle')
      .attr('dy', '0.35em')
      .attr('font-size', '12px')
      .attr('fill', d => d.data.type === 'decision' ? '#000' : '#fff')
      .text(d => d.data.name);
  }, [iaData, error]);

  return (
    <div className="p-5 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold mb-4">IA Flowchart Diagram</h1>
      <input
        type="file"
        accept=".json"
        onChange={handleFileUpload}
        disabled={loading}
        className="mb-2"
      />
      <button onClick={fetchData} disabled={loading} className="ml-2 px-4 py-2 bg-blue-500 text-white rounded">
        Refresh
      </button>
      {loading && <p className="text-blue-500">Uploading...</p>}
      {error && <p className="text-red-500">{error}</p>}
      <svg ref={svgRef} width="800" height="600" className="border border-gray-300 mt-5 bg-white" />
    </div>
  );
}

export default App;
