import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import * as d3 from 'd3';

function App() {
  const [iaData, setIaData] = useState(null);
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [snapToGrid, setSnapToGrid] = useState(false);
  const [expandedNodes, setExpandedNodes] = useState(new Set());
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [width, setWidth] = useState(800);
  const [height, setHeight] = useState(600);
  const svgRef = useRef();
  const zoomRef = useRef();
  const gRef = useRef();
  const simulationRef = useRef();
  const dragLineRef = useRef();

  const colors = useMemo(() => ['#dbeafe', '#dcfce7', '#f3e8ff', '#fef3c7', '#f0f9ff'], []); // Pastel colors for levels

  const hierarchyToGraph = useCallback((data) => {
    const nodes = [];
    const links = [];
    const nodeMap = new Map();

    const traverse = (node, parentId = null, depth = 0) => {
      const id = nodes.length;
      nodes.push({
        id,
        name: node.name,
        x: Math.random() * width,
        y: Math.random() * height,
        depth,
        fx: null,
        fy: null,
      });
      nodeMap.set(node.name, id);

      if (parentId !== null) {
        links.push({ source: parentId, target: id });
      }

      if (node.children) {
        node.children.forEach(child => traverse(child, id, depth + 1));
      }
    };

    traverse(data);
    return { nodes, links };
  }, [width, height]);

  useEffect(() => {
    const handleResize = () => {
      setWidth(window.innerWidth - 40);
      setHeight(window.innerHeight - 200);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.name.endsWith('.json')) {
      setError('Please select a JSON file.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const text = await file.text();
      const jsonData = JSON.parse(text);

      const response = await fetch('http://localhost:3001/upload', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(jsonData),
      });

      if (response.ok) {
        setIaData(jsonData);
        setExpandedNodes(new Set()); // Reset expanded on new upload
        fetchData();
      } else {
        setError('Failed to upload JSON.');
      }
    } catch (err) {
      setError('Invalid JSON file.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (iaData) {
      setGraphData(hierarchyToGraph(iaData));
    }
  }, [iaData, hierarchyToGraph]);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:3001/ia');
      if (response.ok) {
        const data = await response.json();
        setIaData(data);
        setError('');
      } else {
        setIaData(null);
        setError('No IA data uploaded yet.');
      }
    } catch (err) {
      setError('Failed to fetch data.');
    }
  };


  const zoomIn = useCallback(() => {
    if (zoomRef.current) {
      const currentTransform = d3.zoomTransform(svgRef.current);
      zoomRef.current.transform(svgRef.current, currentTransform.scaleBy(1.2));
    }
  }, []);

  const zoomOut = useCallback(() => {
    if (zoomRef.current) {
      const currentTransform = d3.zoomTransform(svgRef.current);
      zoomRef.current.transform(svgRef.current, currentTransform.scaleBy(0.8));
    }
  }, []);

  const fitToScreen = useCallback(() => {
    if (gRef.current && svgRef.current) {
      const bounds = gRef.current.getBBox();
      const fullWidth = width;
      const fullHeight = height;
      const midX = (bounds.x + bounds.width / 2);
      const midY = (bounds.y + bounds.height / 2);
      const scale = 0.85;
      const translate = [fullWidth / 2 - scale * midX, fullHeight / 2 - scale * midY];
      zoomRef.current.transform(svgRef.current, d3.zoomIdentity.translate(translate[0], translate[1]).scale(scale));
    }
  }, [width, height]);

  const resetView = useCallback(() => {
    if (zoomRef.current) {
      zoomRef.current.transform(svgRef.current, d3.zoomIdentity);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (!iaData || error) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    // Add zoom and pan with smooth transitions
    zoomRef.current = d3.zoom()
      .scaleExtent([0.1, 4])
      .on('zoom', (event) => {
        gRef.current.attr('transform', event.transform);
      });

    svg.call(zoomRef.current);

    const g = svg.append('g')
      .attr('ref', gRef);

    // Enhanced defs for arrow markers, shadow, and glow
    const defs = svg.append('defs');

    // Arrowhead
    defs.append('marker')
      .attr('id', 'arrowhead')
      .attr('viewBox', '-0 -5 10 10')
      .attr('refX', 15)
      .attr('refY', 0)
      .attr('orient', 'auto')
      .attr('markerWidth', 8)
      .attr('markerHeight', 8)
      .attr('xoverflow', 'visible')
      .append('path')
      .attr('d', 'M 0,-5 L 10 ,0 L 0,5')
      .attr('fill', '#6b7280')
      .attr('stroke', 'none');

    // Soft shadow
    const shadowFilter = defs.append('filter')
      .attr('id', 'shadow')
      .attr('x', '-50%')
      .attr('y', '-50%')
      .attr('width', '200%')
      .attr('height', '200%');

    shadowFilter.append('feDropShadow')
      .attr('dx', 1)
      .attr('dy', 1)
      .attr('stdDeviation', 3)
      .attr('flood-color', 'rgba(0,0,0,0.2)');

    // Glow for hover
    const glowFilter = defs.append('filter')
      .attr('id', 'glow')
      .attr('x', '-50%')
      .attr('y', '-50%')
      .attr('width', '200%')
      .attr('height', '200%');

    glowFilter.append('feGaussianBlur')
      .attr('stdDeviation', 3)
      .attr('result', 'coloredBlur');

    const feMerge = glowFilter.append('feMerge');
    feMerge.append('feMergeNode').attr('in', 'coloredBlur');
    feMerge.append('feMergeNode').attr('in', 'SourceGraphic');

    const tree = d3.tree().size([height - 100, width - 200]);
    const root = buildHierarchy(iaData);
    const treeData = tree(root);

    // Smooth curved links
    const link = g.selectAll('.link')
      .data(treeData.links())
      .enter().append('g')
      .attr('class', 'link')
      .style('transition', 'stroke 0.3s ease');

    link.append('path')
      .attr('id', (d, i) => `linkPath${i}`)
      .attr('d', d3.linkHorizontal()
        .x(d => d.y)
        .y(d => d.x))
      .attr('fill', 'none')
      .attr('marker-end', 'url(#arrowhead)')
      .attr('stroke', '#9ca3af')
      .attr('stroke-width', 2)
      .style('transition', 'stroke 0.3s ease, stroke-width 0.3s ease');

    // Link labels (if any)
    link.append('text')
      .attr('font-size', '10px')
      .attr('fill', '#6b7280')
      .attr('text-anchor', 'middle')
      .style('pointer-events', 'none')
      .append('textPath')
      .attr('startOffset', '50%')
      .attr('xlink:href', (d, i) => `#linkPath${i}`)
      .text(d => d.target.data.label || '');

    // Nodes with animations, collapse, hover glow
    const node = g.selectAll('.node')
      .data(treeData.descendants())
      .enter().append('g')
      .classed('node', true)
      .attr('transform', `translate(${width / 2}, 50) scale(0.5)`)
      .attr('opacity', 0)
      .style('transition', 'transform 0.5s ease, opacity 0.5s ease')
      .on('click', (event, d) => {
        if (d.children || d._children) {
          toggleNode(d);
        }
      })
      .on('mouseover', function(event, d) {
        d3.select(this).select('rect')
          .attr('filter', 'url(#glow)')
          .attr('stroke', '#3b82f6')
          .attr('stroke-width', 3);
        // Highlight connected links (incoming and outgoing)
        g.selectAll('.link').filter(l => l.source === d || l.target === d)
          .select('path').attr('stroke', '#3b82f6').attr('stroke-width', 3);
      })
      .on('mouseout', function(event, d) {
        d3.select(this).select('rect')
          .attr('filter', 'url(#shadow)')
          .attr('stroke', '#e5e7eb')
          .attr('stroke-width', 1);
        g.selectAll('.link path').attr('stroke', '#9ca3af').attr('stroke-width', 2);
      });

    // Animate nodes in
    node.transition()
      .duration(500)
      .attr('transform', d => `translate(${d.y},${d.x})`)
      .attr('opacity', 1);

    node.append('rect')
      .attr('width', 140)
      .attr('height', 50)
      .attr('x', -70)
      .attr('y', -25)
      .attr('rx', 10)
      .attr('ry', 10)
      .attr('fill', d => colors[d.depth % colors.length] || '#ffffff')
      .attr('stroke', '#e5e7eb')
      .attr('stroke-width', 1)
      .attr('filter', 'url(#shadow)');

    node.append('text')
      .attr('text-anchor', 'middle')
      .attr('x', 0)
      .attr('y', 0)
      .attr('dy', '.35em')
      .attr('font-size', '12px')
      .attr('font-weight', 'bold')
      .attr('fill', '#1f2937')
      .text(d => d.data.name)
      .style('pointer-events', 'none');

    // Expand/collapse indicator (simplified, as full collapse needs _children handling)
    node.filter(d => d.children && d.children.length > 0)
      .append('text')
      .attr('text-anchor', 'end')
      .attr('x', 70)
      .attr('y', 0)
      .attr('font-size', '14px')
      .attr('fill', '#6b7280')
      .text('▶')
      .style('cursor', 'pointer')
      .on('click', (event, d) => {
        event.stopPropagation();
        toggleNode(d);
      });
  }, [iaData, error, width, height, expandedNodes, colors, toggleNode, buildHierarchy]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.target.tagName === 'INPUT' || event.target.tagName === 'TEXTAREA' || event.target.isContentEditable) {
        return; // Don't interfere with input fields
      }

      switch (event.key) {
        case '+':
        case '=':
          event.preventDefault();
          zoomIn();
          break;
        case '-':
          event.preventDefault();
          zoomOut();
          break;
        case 'f':
        case 'F':
          event.preventDefault();
          fitToScreen();
          break;
        case 'r':
        case 'R':
        case '0':
          event.preventDefault();
          resetView();
          break;
        default:
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [zoomIn, zoomOut, fitToScreen, resetView]);

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="p-4 bg-white shadow-sm border-b flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800">IA Flowchart Diagram</h1>
        <div className="flex items-center space-x-2">
          <input
            type="file"
            accept=".json"
            onChange={handleFileUpload}
            disabled={loading}
            className="file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
          />
          <button 
            onClick={fetchData} 
            disabled={loading} 
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50"
          >
            Refresh
          </button>
        </div>
      </div>
      {loading && <p className="p-2 text-blue-500 text-center">Uploading...</p>}
      {error && <p className="p-2 text-red-500 text-center">{error}</p>}
      <div className="flex-1 relative">
        <svg ref={svgRef} width={width} height={height} className="absolute inset-0 w-full h-full bg-white">
          <g ref={gRef}></g>
        </svg>
        {/* Floating Toolbar */}
        <div className="absolute top-4 right-4 bg-white shadow-lg rounded-lg p-2 border flex flex-col space-y-1 z-10">
          <button onClick={zoomIn} className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 rounded text-gray-700">+</button>
          <button onClick={zoomOut} className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 rounded text-gray-700">-</button>
          <button onClick={fitToScreen} className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 rounded text-gray-700">Fit</button>
          <button onClick={resetView} className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 rounded text-gray-700">Reset</button>
        </div>
      </div>
    </div>
  );
}

export default App;
