import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function ReferentPage() {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch('http://localhost:3003/ia-documents');
      if (response.ok) {
        const data = await response.json();
        setDocuments(data.documents || []);
      } else {
        setError('Failed to fetch IA documents.');
      }
    } catch (err) {
      setError('Error fetching IA documents.');
    } finally {
      setLoading(false);
    }
  };

  const generateDocument = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:3003/generate-ia-document', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: 'Generate an IA document' }),
      });
      if (response.ok) {
        const data = await response.json();
        // Assuming the response includes the new document
        setDocuments(prev => [...prev, data.document]);
      } else {
        setError('Failed to generate IA document.');
      }
    } catch (err) {
      setError('Error generating IA document.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="p-4 bg-white shadow-sm border-b flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <Link to="/app" className="px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors">
            ‚Üê Back to IA Flowchart
          </Link>
          <h1 className="text-2xl font-bold text-gray-800">Referent Page - IA Documents</h1>
        </div>
        <button
          onClick={generateDocument}
          disabled={loading}
          className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50"
        >
          Generate New IA Document
        </button>
      </div>
      {loading && <p className="p-2 text-blue-500 text-center">Loading...</p>}
      {error && <div className="p-3 mb-4 text-sm text-red-700 bg-red-100 rounded border border-red-400 text-center" role="alert">{error}</div>}
      <div className="flex-1 p-4">
        <div className="bg-white shadow-lg rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">IA Documents List</h2>
          {documents.length === 0 ? (
            <p className="text-gray-500">No IA documents available. Click "Generate New IA Document" to create one.</p>
          ) : (
            <ul className="space-y-2">
              {documents.map((doc, index) => (
                <li key={index} className="flex justify-between items-center p-3 bg-gray-50 rounded">
                  <span className="font-medium">{doc.title || `Document ${index + 1}`}</span>
                  <a
                    href={doc.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                  >
                    View/Download
                  </a>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default ReferentPage;
